commits.content:
@use 'colors' as c;

.wrapper {
	margin: 0 auto;
	height: 100%;
	width: 100%;
	max-width: 1400px;
}

.section {
	padding: 6em 2em;

	&__heading {
		padding-bottom: 0.2em;
		text-align: center;
		font-size: 3rem;
	}
}

.logo {
	padding: 0.3em 0.6em;
	font-family: 'cinzel-regular', serif;
	font-size: 3rem;
	font-weight: bold;
	text-transform: uppercase;
	color: c.$leading;
	outline: none;
	transition: color 0.3s;

	&:hover,
	&:focus {
		color: c.$leading-brighter;
	}
}

.link-animation {
	display: inline-block;
	position: relative;
	z-index: 1;
	overflow: hidden;

	&::before {
		content: '';
		position: absolute;
		left: -1.5%;
		height: 100%;
		width: 103%;
		transform: scaleX(0);
		background-color: c.$leading-lighter;
		transition: transform 0.3s;
		transform-origin: right;
		z-index: -1;
	}

	&:hover::before,
	&:focus::before {
		transform: scaleX(1);
		transform-origin: left;
	}
}
